import React, { useState, useEffect, useRef } from "react";
import mainSong from "../assets/audio/meri-jaan-song.mp3";

const PhotoAlbum = ({ onBack }) => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showSongTitle, setShowSongTitle] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);
  const audioRef = useRef(null);

  const photos = Array.from({ length: 10 }, (_, i) => ({
    id: i + 1,
    src: `/images/photo${i + 1}.jpg`,
    caption: `Our Beautiful Memory #${i + 1} 💖`,
    message: [
      "Every moment with you is magical ✨",
      "Your smile makes my world complete 🌈",
      "In your eyes, I found my home 🏡",
      "You are my today and all of my tomorrows 💫",
      "My heart beats only for you 💓",
      "You are the answer to every prayer I've ever sent 🌟",
      "With you, every day feels like a fairytale 🏰",
      "You are my sunshine on the cloudiest days ☀️",
      "My love for you grows stronger every day 🌹",
      "Forever and always, it's you 💝",
    ][i],
  }));

  // Auto-play music
  useEffect(() => {
    const playAudio = async () => {
      try {
        await audioRef.current.play();
        setIsPlaying(true);
        setTimeout(() => setShowSongTitle(false), 5000);
      } catch (err) {
        console.log("Autoplay blocked, waiting for user interaction.");
      }
    };
    playAudio();
  }, []);

  // Auto-scroll photos every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % photos.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [photos.length]);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) audioRef.current.pause();
      else audioRef.current.play();
      setIsPlaying(!isPlaying);
    }
  };

  const openModal = (photo) => setSelectedPhoto(photo);
  const closeModal = () => setSelectedPhoto(null);

  return (
    <div className="album-container">
      {/* Background Music */}
      <audio ref={audioRef} loop>
        <source src={mainSong} type="audio/mpeg" />
      </audio>

      {/* Song Title Overlay */}
      {showSongTitle && (
        <div className="song-title-overlay">
          <h1>Tu Maan Meri Jaan 🎵</h1>
          <p>Playing in the background 💖</p>
        </div>
      )}

      {/* Music Control */}
      <div className="music-control">
        <button onClick={togglePlay}>{isPlaying ? "⏸️" : "▶️"}</button>
      </div>

      {/* Photo Display */}
      <div className="photo-display">
        {photos.map((photo, idx) => (
          <div
            key={photo.id}
            className={`photo-frame ${idx === currentIndex ? "active" : ""}`}
            onClick={() => openModal(photo)}
          >
            <h3>{photo.caption}</h3>
            <p>{photo.message}</p>
            <div className="photo-placeholder">{/* Replace with <img src={photo.src}/> */}</div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedPhoto && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-btn" onClick={closeModal}>
              ✕
            </button>
            <h2>{selectedPhoto.caption}</h2>
            <p>{selectedPhoto.message}</p>
          </div>
        </div>
      )}

      <button className="back-btn" onClick={onBack}>
        🏡 Back
      </button>
    </div>
  );
};

export default PhotoAlbum;
