import React, { useState, useEffect, useRef } from "react";
import mainSong from "../assets/audio/meri-jaan-song.mp3";

const PhotoAlbum = ({ onBack }) => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);
  const containerRef = useRef(null);

  const photos = Array.from({ length: 10 }, (_, i) => ({
    id: i + 1,
    src: `/images/photo${i + 1}.jpg`,
    caption: `Our Beautiful Memory #${i + 1} üíñ`,
    message: [
      "Every moment with you is magical ‚ú®",
      "Your smile makes my world complete üåà",
      "In your eyes, I found my home üè°",
      "You are my today and all of my tomorrows üí´",
      "My heart beats only for you üíì",
      "You are the answer to every prayer I've ever sent üåü",
      "With you, every day feels like a fairytale üè∞",
      "You are my sunshine on the cloudiest days ‚òÄÔ∏è",
      "My love for you grows stronger every day üåπ",
      "Forever and always, it's you üíù",
    ][i],
  }));

  // Auto-play music
  useEffect(() => {
    const playAudio = async () => {
      try {
        await audioRef.current.play();
        setIsPlaying(true);
      } catch (err) {
        console.log("Autoplay blocked, waiting for user interaction.");
      }
    };
    playAudio();
  }, []);

  // Auto-scroll photos
  useEffect(() => {
    const interval = setInterval(() => {
      if (containerRef.current) {
        const currentScroll = containerRef.current.scrollTop;
        const photoHeight = containerRef.current.firstChild.offsetHeight + 20; // 20px margin
        const maxScroll =
          containerRef.current.scrollHeight - containerRef.current.clientHeight;

        if (currentScroll + photoHeight > maxScroll) {
          // Scroll back to top
          containerRef.current.scrollTo({ top: 0, behavior: "smooth" });
        } else {
          containerRef.current.scrollTo({
            top: currentScroll + photoHeight,
            behavior: "smooth",
          });
        }
      }
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) audioRef.current.pause();
      else audioRef.current.play();
      setIsPlaying(!isPlaying);
    }
  };

  const openModal = (photo) => setSelectedPhoto(photo);
  const closeModal = () => setSelectedPhoto(null);

  return (
    <div className="album-container">
      <audio ref={audioRef} loop>
        <source src={mainSong} type="audio/mpeg" />
      </audio>

      <div className="music-control">
        <button onClick={togglePlay}>{isPlaying ? "‚è∏Ô∏è" : "‚ñ∂Ô∏è"}</button>
      </div>

      {/* Vertical Photo Scroll */}
      <div className="photo-display" ref={containerRef}>
        {photos.map((photo) => (
          <div
            key={photo.id}
            className="photo-frame"
            onClick={() => openModal(photo)}
          >
            <h3>{photo.caption}</h3>
            <p>{photo.message}</p>
            <div className="photo-placeholder">{/* Replace with <img src={photo.src}/> */}</div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedPhoto && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-btn" onClick={closeModal}>
              ‚úï
            </button>
            <h2>{selectedPhoto.caption}</h2>
            <p>{selectedPhoto.message}</p>
          </div>
        </div>
      )}

      <button className="back-btn" onClick={onBack}>
        üè° Back
      </button>
    </div>
  );
};

export default PhotoAlbum;
