import React, { useState, useEffect } from "react";

const Home = ({ onPinSuccess, onViewCard }) => {
  const [pin, setPin] = useState("");
  const [error, setError] = useState("");
  const [currentHeart, setCurrentHeart] = useState("💖");
  const [floatingHearts, setFloatingHearts] = useState([]);
  const [showSecret, setShowSecret] = useState(false);
  const [typingText, setTypingText] = useState("");
  const [textIndex, setTextIndex] = useState(0);

  const romanticMessages = [
    "Every heartbeat of mine whispers your name... 💓",
    "In your eyes, I found my forever home... 🏡",
    "You are the melody that makes my soul dance... 🎶",
    "My love for you grows stronger with every sunrise... 🌅",
    "You are the dream I never want to wake up from... 💫",
  ];

  // Typing effect for romantic messages
  useEffect(() => {
    if (textIndex < romanticMessages.length) {
      const currentMessage = romanticMessages[textIndex];
      let charIndex = 0;

      const typingInterval = setInterval(() => {
        if (charIndex <= currentMessage.length) {
          setTypingText(currentMessage.slice(0, charIndex));
          charIndex++;
        } else {
          clearInterval(typingInterval);
          setTimeout(() => {
            setTextIndex((prev) => (prev + 1) % romanticMessages.length);
            setTypingText("");
          }, 2000);
        }
      }, 50);

      return () => clearInterval(typingInterval);
    }
  }, [textIndex]);

  // Animated hearts floating background
  useEffect(() => {
    const hearts = ["💖", "💕", "💝", "💘", "💓", "💗", "💞", "🌸", "🌺", "🌷"];
    const interval = setInterval(() => {
      setCurrentHeart(hearts[Math.floor(Math.random() * hearts.length)]);
    }, 800);

    // Create floating hearts
    const heartInterval = setInterval(() => {
      const newHeart = {
        id: Date.now(),
        emoji: hearts[Math.floor(Math.random() * hearts.length)],
        left: Math.random() * 100,
        size: Math.random() * 25 + 15,
        duration: Math.random() * 4 + 3,
      };
      setFloatingHearts((prev) => [...prev.slice(-20), newHeart]);
    }, 500);

    return () => {
      clearInterval(interval);
      clearInterval(heartInterval);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (pin === "220923") {
      setError("");
      // Add success animation before redirecting
      document
        .querySelector(".pin-container")
        .classList.add("success-animation");
      setTimeout(() => {
        onPinSuccess();
      }, 1500);
    } else {
      setError("Incorrect PIN. Our love story deserves the perfect code 💔");
      // Shake animation for wrong PIN
      document.querySelector(".pin-form").classList.add("shake-animation");
      setTimeout(() => {
        document.querySelector(".pin-form").classList.remove("shake-animation");
      }, 500);
    }
  };

  const revealSecret = () => {
    setShowSecret(true);
    // Add reveal animation
    const secretBtn = document.querySelector(".reveal-secret-btn");
    secretBtn.style.display = "none";
  };

  return (
    <div className="emotional-home-container">
      {/* Floating Elements Background */}
      {floatingHearts.map((heart) => (
        <div
          key={heart.id}
          className="floating-heart"
          style={{
            left: `${heart.left}%`,
            fontSize: `${heart.size}px`,
            animationDuration: `${heart.duration}s`,
          }}
        >
          {heart.emoji}
        </div>
      ))}

      {/* Sparkle Effects */}
      <div className="sparkle-effect">✨</div>
      <div className="sparkle-effect sparkle-2">🌟</div>
      <div className="sparkle-effect sparkle-3">💫</div>

      {/* Main Content */}
      <div className="emotional-content">
        {/* Her Picture Section with Enhanced Design */}
        <div className="her-picture-section">
          <div className="picture-frame-glow">
            <div className="picture-frame">
              <div className="photo-placeholder">
                <div className="photo-frame">
                  <div className="photo-glow"></div>
                  <img
                    src="/images/her-photo.jpg"
                    alt="My Beautiful Angel"
                    className="her-photo"
                    onError={(e) => {
                      e.target.style.display = "none";
                      e.target.nextSibling.style.display = "flex";
                    }}
                  />
                  <div className="photo-fallback">
                    <div className="fallback-content">
                      <div className="fallback-heart">💝</div>
                      <h3>My Beautiful Angel</h3>
                      <p>
                        You're the most beautiful sight my eyes have ever seen
                        👼
                      </p>
                    </div>
                  </div>
                </div>
              </div>
              <div className="frame-decoration">✨</div>
              <div className="frame-decoration frame-2">🌹</div>
              <div className="frame-decoration frame-3">💕</div>
            </div>
          </div>
        </div>

        {/* Emotional Text Section with Typing Effect */}
        <div className="emotional-text-section">
          <div className="heart-pulse">{currentHeart}</div>

          <p className="emotional-subtitle">
            You make every moment feel like magic... ✨
          </p>

          {/* Typing Text Effect */}
          <div className="typing-container">
            <div className="typing-text">{typingText}</div>
            <div className="typing-cursor">|</div>
          </div>

          <div className="romantic-message-bubble">
            <div className="message-scroll">
              <p>🌹 "Your love paints colors in my monochrome world"</p>
              <p>💫 "With you, every ordinary moment becomes extraordinary"</p>
              <p>🌟 "You're the reason I believe in forever"</p>
              <p>💕 "My heart found its home in your smile"</p>
              <p>✨ "You are my today and all of my tomorrows"</p>
            </div>
          </div>
        </div>

        {/* Secret PIN Section with Enhanced Reveal */}
        <div className="secret-section">
          {!showSecret ? (
            <button className="reveal-secret-btn" onClick={revealSecret}>
              <span className="btn-sparkle">✨</span>
              🔒 Discover Our Secret Garden 💐
              <span className="btn-sparkle">✨</span>
            </button>
          ) : (
            <div className="pin-container-reveal">
              <div className="secret-garden">
                <h2>💐 Welcome to Our Secret Garden 💐</h2>
                <p className="pin-instruction">
                  Where our most precious memories bloom... 🌸
                </p>

                <form onSubmit={handleSubmit} className="pin-form">
                  <div className="input-container">
                    <input
                      type="password"
                      className="emotional-pin-input"
                      value={pin}
                      onChange={(e) => setPin(e.target.value)}
                      placeholder="💞 Enter our secret code"
                      maxLength="6"
                    />
                    <div className="input-glow"></div>
                  </div>
                  <br />
                  <button type="submit" className="emotional-submit-btn">
                    <span className="btn-text">
                      🌸 Unlock Our Love Story 🌸
                    </span>
                    <div className="btn-shine"></div>
                  </button>
                </form>

                {error && <p className="error-message">{error}</p>}

                <div className="alternative-options">
                  <button onClick={onViewCard} className="emotional-card-btn">
                    💌 Let My Heart Speak to You...
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Romantic Footer with Enhanced Design */}
        <div className="romantic-footer">
          <div className="footer-hearts">💖 💕 💝 💘 💓 💗 💞</div>
          <p>Crafted with endless love and infinite admiration 💫</p>
          <p>For the one who makes my world complete 🌍</p>
          <div className="footer-sparkle">✨</div>
        </div>
      </div>

      {/* Background Music Toggle */}
      <div className="music-toggle">
        <button className="music-btn">🎵</button>
      </div>
    </div>
  );
};

export default Home;
