import React, { useState } from 'react';
import './BirthdayWish.css'; // We'll use this for some styling

const BirthdayWish = () => {
  const [wishClicked, setWishClicked] = useState(false);
  const [pinEntered, setPinEntered] = useState('');
  const [accessGranted, setAccessGranted] = useState(false);
  const [pinError, setPinError] = useState('');

  // Replace with your own birthday song URL
  const birthdaySong = 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3';

  // Replace with your private photos URLs
  const photos = [
    'https://via.placeholder.com/300x200?text=Photo+1',
    'https://via.placeholder.com/300x200?text=Photo+2',
    'https://via.placeholder.com/300x200?text=Photo+3',
    'https://via.placeholder.com/300x200?text=Photo+4',
    'https://via.placeholder.com/300x200?text=Photo+5',
    'https://via.placeholder.com/300x200?text=Photo+6',
    'https://via.placeholder.com/300x200?text=Photo+7',
    'https://via.placeholder.com/300x200?text=Photo+8',
    'https://via.placeholder.com/300x200?text=Photo+9',
    'https://via.placeholder.com/300x200?text=Photo+10',
  ];

  const correctPin = '123456'; // Change this to your secret PIN

  const handlePinSubmit = (e) => {
    e.preventDefault();
    if (pinEntered === correctPin) {
      setAccessGranted(true);
      setPinError('');
    } else {
      setPinError('Incorrect PIN, try again!');
    }
  };

  return (
    <div className="birthday-container">
      {!wishClicked && (
        <button className="birthday-button" onClick={() => setWishClicked(true)}>
          üéâ Click here for your special birthday wish! üéâ
        </button>
      )}

      {wishClicked && (
        <div className="wish-section">
          <h1>Happy Birthday! ü•≥</h1>
          <p>
            Wishing you a day filled with love, laughter, and unforgettable memories. You deserve all
            the happiness in the world! ‚ù§Ô∏è
          </p>
          <audio controls autoPlay>
            <source src={birthdaySong} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>

          {!accessGranted && (
            <div className="pin-section">
              <h3>Enter your 6-digit PIN to see your private photo album:</h3>
              <form onSubmit={handlePinSubmit}>
                <input
                  type="password"
                  maxLength="6"
                  value={pinEntered}
                  onChange={(e) => setPinEntered(e.target.value)}
                  placeholder="Enter PIN"
                />
                <button type="submit">Submit</button>
              </form>
              {pinError && <p className="error">{pinError}</p>}
            </div>
          )}

          {accessGranted && (
            <div className="photo-album">
              <h2>Your Private Photo Album üì∏</h2>
              <div className="photos-grid">
                {photos.map((photo, index) => (
                  <img key={index} src={photo} alt={`Birthday ${index + 1}`} />
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BirthdayWish;
