import React, { useState, useEffect, useRef } from "react";
import mainSong from '../assets/audio/meri-jaan-song.mp3'; // relative to the file

const PhotoAlbum = ({ onBack }) => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showSongTitle, setShowSongTitle] = useState(true);
  const audioRef = useRef(null);

  // Auto-play music when component mounts
  useEffect(() => {
    const playAudio = async () => {
      try {
        await audioRef.current.play();
        setIsPlaying(true);

        // Hide song title after 5 seconds
        setTimeout(() => {
          setShowSongTitle(false);
        }, 5000);
      } catch (error) {
        console.log("Auto-play failed, waiting for user interaction");
      }
    };

    playAudio();
  }, []);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  // Replace these with your actual photo paths
  const photos = Array.from({ length: 10 }, (_, i) => ({
    id: i + 1,
    src: `/images/photo${i + 1}.jpg`,
    caption: `Our Beautiful Memory #${i + 1} 💖`,
    message: [
      "Every moment with you is magical ✨",
      "Your smile makes my world complete 🌈",
      "In your eyes, I found my home 🏡",
      "You are my today and all of my tomorrows 💫",
      "My heart beats only for you 💓",
      "You are the answer to every prayer I've ever sent 🌟",
      "With you, every day feels like a fairytale 🏰",
      "You are my sunshine on the cloudiest days ☀️",
      "My love for you grows stronger every day 🌹",
      "Forever and always, it's you 💝",
    ][i],
  }));

  const openModal = (photo) => {
    setSelectedPhoto(photo);
  };

  const closeModal = () => {
    setSelectedPhoto(null);
  };

  return (
    <div className="emotional-album-container">
      {/* Background Music */}
      <audio
        ref={audioRef}
        loop
        onPlay={() => setIsPlaying(true)}
        onPause={() => setIsPlaying(false)}
      >
        <source src={mainSong} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>

      {/* Song Title Overlay */}
      {showSongTitle && (
        <div className="song-title-overlay">
          <div className="song-title-content">
            <div className="music-note">🎵</div>
            <h2>Our Special Song</h2>
            <h1>"Tu Maan Meri Jaan"</h1>
            <p>Playing in the background... 💖</p>
            <div className="loading-bar">
              <div className="loading-progress"></div>
            </div>
          </div>
        </div>
      )}

      {/* Music Control */}
      <div className="music-control">
        <button onClick={togglePlay} className="music-btn">
          {isPlaying ? "⏸️" : "▶️"}
        </button>
        <span className="song-name">
          {isPlaying ? "Tu Maan Meri Jaan 🎵" : "Click to play 💕"}
        </span>
      </div>

      {/* Main Album Content */}
      <div className="emotional-album-content">
        <h1 className="emotional-album-title">Our Journey Together 💫</h1>

        <p className="album-subtitle">
          Every photograph tells the story of my love for you... 📖
        </p>

        <div className="romantic-intro">
          <p>
            💖 "These memories are the treasures I hold closest to my heart"
          </p>
          <p>✨ "Each moment with you is a page in our beautiful love story"</p>
        </div>

        <div className="emotional-photos-grid">
          {photos.map((photo) => (
            <div
              key={photo.id}
              className="emotional-photo-item"
              onClick={() => openModal(photo)}
            >
              <div className="photo-frame-album">
                <div className="photo-number">#{photo.id}</div>
                <div className="photo-placeholder-album">
                  {/* Replace with actual image */}
                  <div className="album-photo-frame">
                    <div className="album-photo-content">
                      Memory {photo.id} 💕
                      {/* Actual image: <img src={photo.src} alt={photo.caption} /> */}
                    </div>
                  </div>
                </div>
                <div className="photo-caption">
                  <h3>{photo.caption}</h3>
                  <p className="photo-message">{photo.message}</p>
                </div>
                <div className="photo-hover-effect">
                  <span>Click to View 💝</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Love Message */}
        <div className="final-love-message">
          <h2>💌 To My Everything</h2>
          <p>
            "Each of these photos holds a piece of my heart. From the first
            moment I saw you, I knew my life would never be the same. You are
            the melody in my song, the color in my world, and the beat in my
            heart. Forever yours... 🌹"
          </p>
          <div className="floating-hearts">💖 💕 💝 💘 💓 💗 💞</div>
        </div>
      </div>

      {/* Photo Modal */}
      {selectedPhoto && (
        <div className="emotional-modal" onClick={closeModal}>
          <div
            className="emotional-modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            <button className="close-modal-btn" onClick={closeModal}>
              ✕
            </button>

            <div className="modal-photo-container">
              <div className="modal-photo">
                {/* Replace with actual image */}
                <div className="modal-photo-placeholder">
                  <h2>Memory #{selectedPhoto.id}</h2>
                  <p>{selectedPhoto.caption}</p>
                  {/* Actual image: <img src={selectedPhoto.src} alt={selectedPhoto.caption} /> */}
                </div>
              </div>

              <div className="modal-message">
                <h3>💝 My Heart's Whisper</h3>
                <p className="romantic-quote">{selectedPhoto.message}</p>
                <div className="message-decoration">✨ 🌹 💫</div>
              </div>
            </div>
          </div>
        </div>
      )}

      <button onClick={onBack} className="emotional-back-btn">
        🏡 Back to Our Special Place
      </button>
    </div>
  );
};

export default PhotoAlbum;
