import React, { useState } from 'react';

const Home = ({ onPinSuccess, onViewCard }) => {
  const [pin, setPin] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (pin === '220923') {
      setError('');
      onPinSuccess();
    } else {
      setError('Incorrect PIN. Please try again.');
    }
  };

  return (
    <div className="home-container">
      <div className="heart-animation">üíñ</div>
      <h1 className="home-title">Happy Birthday, My Love! üíù</h1>
      <p className="home-subtitle">You mean the world to me...</p>

      <div className="pin-container">
        <h2>Enter Our Special PIN</h2>
        <p>To unlock your surprise album</p>

        <form onSubmit={handleSubmit}>
          <input
            type="password"
            className="pin-input"
            value={pin}
            onChange={(e) => setPin(e.target.value)}
            placeholder="Enter 6-digit PIN"
            maxLength="6"
          />
          <br />
          <button type="submit" className="submit-btn">
            Unlock My Heart üíï
          </button>
        </form>

        {error && <p style={{color: '#ff6b6b', marginTop: '10px'}}>{error}</p>}

        <button onClick={onViewCard} className="view-card-btn">
          Read My Heartfelt Message üíå
        </button>
      </div>

      <div style={{marginTop: '50px', opacity: '0.7'}}>
        <p>Made with üíñ especially for you</p>
      </div>
    </div>
  );
};

export default Home;
