import React, { useState, useEffect } from 'react';

const Home = ({ onPinSuccess, onViewCard }) => {
  const [pin, setPin] = useState('');
  const [error, setError] = useState('');
  const [currentHeart, setCurrentHeart] = useState('üíñ');
  const [floatingHearts, setFloatingHearts] = useState([]);

  // Animated hearts floating background
  useEffect(() => {
    const hearts = ['üíñ', 'üíï', 'üíù', 'üíò', 'üíì', 'üíó', 'üíû'];
    const interval = setInterval(() => {
      setCurrentHeart(hearts[Math.floor(Math.random() * hearts.length)]);
    }, 1000);

    // Create floating hearts
    const heartInterval = setInterval(() => {
      const newHeart = {
        id: Date.now(),
        emoji: hearts[Math.floor(Math.random() * hearts.length)],
        left: Math.random() * 100,
        size: Math.random() * 20 + 10,
        duration: Math.random() * 3 + 2
      };
      setFloatingHearts(prev => [...prev.slice(-15), newHeart]);
    }, 800);

    return () => {
      clearInterval(interval);
      clearInterval(heartInterval);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (pin === '220923') {
      setError('');
      onPinSuccess();
    } else {
      setError('Incorrect PIN. This special moment deserves the right code üíî');
    }
  };

  return (
    <div className="emotional-home-container">
      {/* Floating Hearts Background */}
      {floatingHearts.map(heart => (
        <div
          key={heart.id}
          className="floating-heart"
          style={{
            left: `${heart.left}%`,
            fontSize: `${heart.size}px`,
            animationDuration: `${heart.duration}s`
          }}
        >
          {heart.emoji}
        </div>
      ))}

      {/* Main Content */}
      <div className="emotional-content">
        {/* Her Picture Section */}
        <div className="her-picture-section">
          <div className="picture-frame">
            <div className="photo-placeholder">
              {/* Replace this with her actual photo */}
              <div className="photo-frame">
                <div className="photo-overlay-text">
                  My Beautiful Angel üëº
                </div>
              </div>
              {/* Actual image would be: <img src="/images/her-photo.jpg" alt="My Beautiful Angel" /> */}
            </div>
            <div className="frame-decoration">‚ú®</div>
          </div>
        </div>

        {/* Emotional Text Section */}
        <div className="emotional-text-section">
          <div className="heart-pulse">{currentHeart}</div>

          <h1 className="emotional-main-title">
            To The Most Beautiful Soul I Know üåπ
          </h1>

          <p className="emotional-subtitle">
            Every day with you feels like a beautiful dream... ‚ú®
          </p>

          <div className="romantic-message">
            <p>üí´ "In a sky full of stars, you're my brightest constellation"</p>
            <p>üåô "Your smile lights up my world brighter than a thousand moons"</p>
            <p>üåü "You are the missing piece I never knew I was searching for"</p>
          </div>
        </div>

        {/* Secret PIN Section - Initially Hidden */}
        <div className="secret-section">
          <button 
            className="reveal-secret-btn"
            onClick={() => document.querySelector('.pin-container').style.display = 'block'}
          >
            üîí Unlock Our Secret World üí´
          </button>

          <div className="pin-container" style={{display: 'none'}}>
            <h2>üíù Enter Our Special Code</h2>
            <p className="pin-instruction">
              The numbers that hold our precious memories... üìÖ
            </p>

            <form onSubmit={handleSubmit} className="pin-form">
              <input
                type="password"
                className="emotional-pin-input"
                value={pin}
                onChange={(e) => setPin(e.target.value)}
                placeholder="üíû Our 6-digit code"
                maxLength="6"
              />
              <br />
              <button type="submit" className="emotional-submit-btn">
                üåü Unlock My Heart's Treasure üåü
              </button>
            </form>

            {error && <p className="error-message">{error}</p>}

            <button onClick={onViewCard} className="emotional-card-btn">
              üíå Read What My Heart Wants To Say...
            </button>
          </div>
        </div>

        {/* Romantic Footer */}
        <div className="romantic-footer">
          <p>Made with infinite love and countless heartbeats üíì</p>
          <p>For the one who makes every moment magical ‚ú®</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
