import React, { useState } from 'react';

const PhotoAlbum = ({ onBack }) => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);

  // Replace these with your actual photo paths
  const photos = Array.from({ length: 10 }, (_, i) => ({
    id: i + 1,
    src: `/images/photo${i + 1}.jpg`, // Put your photos in public/images folder
    caption: `Our Beautiful Memory #${i + 1} üíñ`
  }));

  const openModal = (photo) => {
    setSelectedPhoto(photo);
  };

  const closeModal = () => {
    setSelectedPhoto(null);
  };

  return (
    <div className="album-container">
      <h1 className="album-title">Our Special Memories üí´</h1>
      <p style={{ fontSize: '1.2rem', marginBottom: '30px' }}>
        Every moment with you is precious... ‚ú®
      </p>

      <div className="photos-grid">
        {photos.map(photo => (
          <div key={photo.id} className="photo-item" onClick={() => openModal(photo)}>
            <div style={{
              width: '100%',
              height: '300px',
              background: 'linear-gradient(45deg, #ff9a9e, #fad0c4)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1.5rem'
            }}>
              Photo {photo.id} üíï
              {/* Replace with actual image: <img src={photo.src} alt={photo.caption} /> */}
            </div>
            <div className="photo-overlay">
              <h3>{photo.caption}</h3>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for enlarged photo */}
      {selectedPhoto && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0,0,0,0.8)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 1000
        }} onClick={closeModal}>
          <div style={{
            background: 'white',
            padding: '20px',
            borderRadius: '10px',
            maxWidth: '90%',
            maxHeight: '90%'
          }} onClick={e => e.stopPropagation()}>
            <div style={{
              width: '400px',
              height: '400px',
              background: 'linear-gradient(45deg, #ff9a9e, #fad0c4)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: '#333',
              fontSize: '1.5rem'
            }}>
              Photo {selectedPhoto.id} üíï
              {/* Replace with: <img src={selectedPhoto.src} alt={selectedPhoto.caption} style={{maxWidth: '100%', maxHeight: '100%'}} /> */}
            </div>
            <p style={{marginTop: '10px', color: '#333'}}>{selectedPhoto.caption}</p>
            <button onClick={closeModal} style={{
              marginTop: '10px',
              padding: '10px 20px',
              background: '#ff6b6b',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}>
              Close
            </button>
          </div>
        </div>
      )}

      <button onClick={onBack} className="back-btn">
        Back to Home üè†
      </button>
    </div>
  );
};

export default PhotoAlbum;
