import React, { useState, useEffect, useRef } from "react";
import mainSong from "../assets/audio/meri-jaan-song.mp3";

// Import all start images
import start1 from "../assets/photos/start1.jpeg";
import start2 from "../assets/photos/start2.jpeg";
import start3 from "../assets/audio/start3.jpeg";
import start4 from "../assets/audio/start4.jpeg";
import start5 from "../assets/photos/start5.jpeg";
import start6 from "../assets/audio/start6.jpeg";
import start7 from "../assets/photos/start7.jpeg";
import start8 from "../assets/photos/start8.jpeg";
import start9 from "../assets/photos/start9.jpeg";
import start10 from "../assets/photos/start10.jpeg";
import start11 from "../assets/photos/start11.jpeg";
import start12 from "../assets/photos/start12.jpeg";
import start13 from "../assets/photos/start13.jpeg";
import start14 from "../assets/photos/start14.jpeg";
import start15 from "../assets/photos/start15.jpeg"

const PhotoAlbum = ({ onBack }) => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);
  const containerRef = useRef(null);

  // Array of start images
  const startImages = [
    start1, start2, start3, start4, start5, start6, start7,
    start8, start9, start10, start11, start12, start13, start14, start15,
  ];

  const photos = Array.from({ length: 15 }, (_, i) => ({
    id: i + 1,
    src: startImages[i],
    caption: `Our Beautiful Memory #${i + 1} 💖`,
    message: [
      "Every moment with you is magical ✨",
      "Your smile makes my world complete 🌈",
      "In your eyes, I found my home 🏡",
      "You are my today and all of my tomorrows 💫",
      "My heart beats only for you 💓",
      "You are the answer to every prayer I've ever sent 🌟",
      "With you, every day feels like a fairytale 🏰",
      "You are my sunshine on the cloudiest days ☀️",
      "My joy grows with every memory we make 💖",
      "Forever friends, forever memories 🌟",
      "Adventures, laughter, and endless fun 🎉",
      "You're my partner in all silly and happy moments 😄",
      "Every giggle with you is priceless 💛",
      "Life’s brighter because of you 🌈",
      "Together, we make the best memories 💫",
    ][i],
  }));

  // Auto-play music when possible
  useEffect(() => {
    const playAudio = async () => {
      try {
        await audioRef.current.play();
        setIsPlaying(true);
      } catch {
        console.log("Autoplay blocked until user interacts.");
      }
    };
    playAudio();
  }, []);

  // Auto-scroll every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      if (containerRef.current) {
        const { scrollTop, scrollHeight, clientHeight, firstChild } =
          containerRef.current;

        const photoHeight = firstChild?.offsetHeight + 20; // including margin
        const maxScroll = scrollHeight - clientHeight;

        if (scrollTop + photoHeight >= maxScroll) {
          containerRef.current.scrollTo({ top: 0, behavior: "smooth" });
        } else {
          containerRef.current.scrollTo({
            top: scrollTop + photoHeight,
            behavior: "smooth",
          });
        }
      }
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const togglePlay = () => {
    if (isPlaying) audioRef.current.pause();
    else audioRef.current.play();
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="album-container">
      <audio ref={audioRef} loop>
        <source src={mainSong} type="audio/mpeg" />
      </audio>

      <div className="music-control">
        <button onClick={togglePlay}>{isPlaying ? "⏸️ Pause" : "▶️ Play"}</button>
      </div>

      {/* Scrollable photo list */}
      <div className="photo-display" ref={containerRef}>
        {photos.map((photo) => (
          <div
            key={photo.id}
            className="photo-frame"
            onClick={() => setSelectedPhoto(photo)}
          >
            <h3>{photo.caption}</h3>
            <p>{photo.message}</p>
            <div className="photo-placeholder">
              <img src={photo.src} alt={photo.caption} style={{ width: "100%", borderRadius: "10px" }} />
            </div>
          </div>
        ))}
      </div>

      {selectedPhoto && (
        <div className="modal-overlay" onClick={() => setSelectedPhoto(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-btn" onClick={() => setSelectedPhoto(null)}>
              ✕
            </button>
            <h2>{selectedPhoto.caption}</h2>
            <p>{selectedPhoto.message}</p>
            <img src={selectedPhoto.src} alt={selectedPhoto.caption} style={{ width: "100%", borderRadius: "10px" }} />
          </div>
        </div>
      )}

      <button className="back-btn" onClick={onBack}>
        🏡 Back
      </button>
    </div>
  );
};

export default PhotoAlbum;
